import React, { useState, useEffect } from 'react';
import { MdAddShoppingCart } from 'react-icons/md';

import { ProductList } from './styles';
import { api } from '../../services/api';
import { formatPrice } from '../../util/format';
import { useCart } from '../../hooks/useCart';

interface Product {
  id: number;
  title: string;
  price: number;
  image: string;
}

interface ProductFormatted extends Product {
  priceFormatted: string;
}

interface CartItemsAmount {
  [key: number]: number;
}

const Home = (): JSX.Element => {
  const [products, setProducts] = useState<ProductFormatted[]>([]);
  const { addProduct, updateProductAmount, cart } = useCart();

  const cartItemsAmount = cart.reduce((sumAmount, product) => {
    sumAmount[product.id] = product.amount;
    return sumAmount
  }, {} as CartItemsAmount)

  console.log(cartItemsAmount)

  useEffect(() => {
    async function loadProducts() {
      await api.get('http://localhost:3333/products')
      .then(async (response) => {
        const productsArray: ProductFormatted[] = await response.data

        if (productsArray) {

          const finalProducts = productsArray.map(product => !product.priceFormatted ? {
              ...product,
              formatedPrice: formatPrice(product.price)
            } : product
          )
          setProducts(finalProducts)
        }

      })
    }
    loadProducts();
  }, []);

  function handleAddProduct(id: number) {

    if (cart) {
      console.log(cart)
      const filteredCart = cart.filter(product => product.id === id);

      console.log(filteredCart.length)
      if (filteredCart.length > 0) {
        const amount = filteredCart[0].amount +1;

        updateProductAmount({productId: id, amount })
        return
      }

        addProduct(id);
        return


    }
  }

  return (
    <ProductList>
      {products.map(
        product => {
          return(
            <li key={product.id}>
              <img src={product.image} alt="Tênis de Caminhada Leve Confortável" />
              <strong>{product.title}</strong>
              <span>{product.priceFormatted}</span>
              <button
                type="button"
                data-testid="add-product-button"
              onClick={() => handleAddProduct(product.id)}
              >
                <div data-testid="cart-product-quantity">
                  <MdAddShoppingCart size={16} color="#FFF" />
                  {cartItemsAmount[product.id] || 0}
                </div>

                <span>ADICIONAR AO CARRINHO</span>
              </button>
            </li>
          )
        }
      )}
    </ProductList>
  );
};

export default Home;
