import { createContext, ReactNode, useContext, useState } from 'react';
import { toast } from 'react-toastify';
import { api } from '../services/api';
import { Product, Stock } from '../types';

interface CartProviderProps {
  children: ReactNode;
}

interface UpdateProductAmount {
  productId: number;
  amount: number;
}

interface CartContextData {
  cart: Product[];
  addProduct: (productId: number) => Promise<void>;
  removeProduct: (productId: number) => void;
  updateProductAmount: ({ productId, amount }: UpdateProductAmount) => void;
}

const CartContext = createContext<CartContextData>({} as CartContextData);

export function CartProvider({ children }: CartProviderProps): JSX.Element {
  const [cart, setCart] = useState<Product[]>(() => {
    const storagedCart = localStorage.getItem('@RocketShoes:cart')

    if (storagedCart) {
      return JSON.parse(storagedCart);
    }

    return [];
  })

  const addProduct = async (productId: number) => {
    try {
      api.get(`/stock/?id=${productId}`).then(response => {
        api.get(`/products/?id=${productId}`).then(response => {
          const product = response.data[0];
          product.amount = 1;
          const newCart = [...cart, response.data[0] ]
          setCart(newCart);
          localStorage.setItem('@RocketShoes:cart', JSON.stringify(newCart));
        })
      }
      )
    } catch {
      toast.error('Não foi possível adicionar este produto!');
    }
  };

  const removeProduct = (productId: number) => {
    try {

      const newCart = cart.filter(product => productId !== product.id);

      setCart(newCart);
      localStorage.setItem('@RocketShoes:cart', JSON.stringify(newCart));
      toast.success('Produto Removido com sucesso!');

    } catch {
      toast.error('Não foi possível remover este produto!');
    }
  };

  const updateProductAmount = async ({
    productId,
    amount,
  }: UpdateProductAmount) => {
    try {
      api.get(`/stock/?id=${productId}`).then(response => {
        const productStock = response.data[0].amount;

        if(amount <= 0 ) {
          throw new Error()
        }

        if(productStock < amount) {
          toast.error('Quantidade solicitada fora de estoque')    

        }

        let newCart = cart;
        newCart = newCart.map(product => 
          product.id === productId ? {
            ...product, amount: amount
        } : product);

        setCart(newCart);
        localStorage.setItem('@RocketShoes:cart', JSON.stringify(newCart));
        return      

      })
    } catch {
      toast.error('Não foi possível atualizar o carrinho!');
    }
  };

  return (
    <CartContext.Provider
      value={{ cart, addProduct, removeProduct, updateProductAmount }}
    >
      {children}
    </CartContext.Provider>
  );
}

export function useCart(): CartContextData {
  const context = useContext(CartContext);

  return context;
}
